const DownloadList = () => {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [downloadList, setDownloadList] = useState([]);
  const [resources, setResources] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [debugInfo, setDebugInfo] = useState(null);
  const [isDownloading, setIsDownloading] = useState(null);
  const [downloadProgress, setDownloadProgress] = useSimulatedProgress(isDownloading);
  const [options, setOptions] = useState({
    ignoreNoContentFile: true,
    beautifyFile: true,
  });

  const handleAddUrl = useCallback(async () => {
    if (!url.trim()) return;

    setIsScraping(true);
    setError('');
    setSuccess('');
    setDebugInfo(null);

    try {
      const response = await axios.post('/api/scrape-debug', {
        url: url.trim(),
        options
      });

      if (response.data.success) {
        setResources(response.data.resources);
        setDownloadList(prev => [...prev, { 
          url: url.trim(), 
          resources: response.data.resources,
          debug: response.data.debug
        }]);
        setUrl('');
        setSuccess(`Successfully scraped ${response.data.count} resources from ${url.trim()}`);
        setDebugInfo(response.data.debug);
      }
    } catch (error) {
      console.error('Scraping error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to scrape website. Please check the URL and try again.';
      setError(errorMessage);
    } finally {
      setIsScraping(false);
    }
  }, [url, options]);

  const handleDownload = useCallback(async (item) => {
    setIsDownloading(item.url);
    try {
      const response = await axios.post('/api/download', {
        resources: item.resources,
        options
      }, {
        responseType: 'blob'
      });

      setDownloadProgress(100);

      // Create download link
      const blob = new Blob([response.data]);
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = `${new URL(item.url).hostname}.zip`;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(downloadUrl);

      setSuccess(`Downloaded ${item.resources.length} resources as ZIP file`);
    } catch (error) {
      console.error('Download error:', error);
      setError('Failed to download file. Please try again.');
    } finally {
      setTimeout(() => {
        setIsDownloading(null);
      }, 500);
    }
  }, [options, setDownloadProgress]);

  const handleRemoveItem = useCallback((index) => {
    setDownloadList(prev => prev.filter((_, i) => i !== index));
  }, []);

  const handleReset = useCallback(() => {
    setDownloadList([]);
    setResources([]);
    setError('');
    setSuccess('');
    setDebugInfo(null);
  }, []);

  const handleOptionChange = useCallback((key, value) => {
    setOptions(prev => ({ ...prev, [key]: value }));
  }, []);

  const estimateDownloadTime = (bytes) => {
    if (!bytes) return '';
    // Assume average 5 Mbps connection
    const bits = bytes * 8;
    const speed = 5 * 1000 * 1000; // 5 Mbps in bits
    const seconds = Math.ceil(bits / speed);

    if (seconds < 2) return '~1s';
    if (seconds < 60) return `~${seconds}s`;

    const minutes = Math.ceil(seconds / 60);
    return `~${minutes}min`;
  };

  const formatFileSize = (bytes) => {
    if (!bytes || bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <DownloadListContainer>
      <DownloadListHeader>Download List</DownloadListHeader>

      <OptionsSection>
        <OptionItem>
          <OptionLabel>Ignore files with no content</OptionLabel>
          <Toggle
            type="checkbox"
            checked={options.ignoreNoContentFile}
            onChange={(e) => handleOptionChange('ignoreNoContentFile', e.target.checked)}
          />
        </OptionItem>
        <OptionItem>
          <OptionLabel>Beautify files (HTML, CSS, JS, JSON)</OptionLabel>
          <Toggle
            type="checkbox"
            checked={options.beautifyFile}
            onChange={(e) => handleOptionChange('beautifyFile', e.target.checked)}
          />
        </OptionItem>
      </OptionsSection>

      <UrlInputSection>
        <UrlInput
          type="url"
          placeholder="Enter website URL (e.g., https://example.com)"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleAddUrl()}
          disabled={isScraping}
        />
        <ButtonGroup>
          <Button 
            onClick={handleAddUrl} 
            disabled={isScraping || !url.trim()}
            color="primary"
          >
            {isScraping ? (
              <><Spinner /> Scraping...</>
            ) : (
              <><FaPlus /> Add URL</>
            )}
          </Button>
          <Button 
            onClick={() => {
              setDownloadList([]);
              setResources([]);
              setError('');
              setSuccess('');
              setDebugInfo(null);
            }} 
            color="danger"
            disabled={isScraping}
          >
            Reset
          </Button>
        </ButtonGroup>

        {error && (
          <ErrorMessage>
            <FaExclamationTriangle /> {error}
          </ErrorMessage>
        )}

        {debugInfo && (
          <DebugInfo>
            <DebugTitle>
              <FaCheckCircle /> Scrape Successful
            </DebugTitle>
            <DebugGrid>
              <DebugItem>
                <DebugKey>Total Resources</DebugKey>
                <DebugValue>{debugInfo.totalResources}</DebugValue>
              </DebugItem>
              <DebugItem>
                <DebugKey>Total Size</DebugKey>
                <DebugValue>{formatFileSize(debugInfo.totalSize)}</DebugValue>
              </DebugItem>
              <DebugItem>
                <DebugKey>Scrape Time</DebugKey>
                <DebugValue>{debugInfo.scrapeTime}ms</DebugValue>
              </DebugItem>
              <DebugItem>
                <DebugKey>Unique Domains</DebugKey>
                <DebugValue>{debugInfo.uniqueDomains}</DebugValue>
              </DebugItem>
            </DebugGrid>
          </DebugInfo>
        )}
      </UrlInputSection>

      {downloadList.length > 0 ? (
        <DownloadListItems>
          {downloadList.map((item, index) => (
            <React.Fragment key={index}>
              <DownloadListItem highlighted={index === 0}>
                <div>
                  <ItemUrl>{item.url}</ItemUrl>
                  {item.debug && (
                    <ItemInfo>
                      {item.debug.totalResources} resources • {formatFileSize(item.debug.totalSize)} • {item.debug.scrapeTime}ms
                    </ItemInfo>
                  )}
                </div>
                <ItemActions>
                  <Button 
                    onClick={() => handleDownload(item)}
                    disabled={isDownloading === item.url}
                    color="primary"
                    progress={isDownloading === item.url ? downloadProgress : 0}
                  >
                    {isDownloading === item.url ? (
                      `Downloading... ${downloadProgress}%`
                    ) : (
                      <><FaDownload /> Download</>
                    )}
                  </Button>
                  <Button 
                    onClick={() => handleRemoveItem(item.url)} 
                    color="danger"
                  >
                    <FaTrash />
                  </Button>
                </ItemActions>
              </DownloadListItem>
            </React.Fragment>
          ))}
        </DownloadListItems>
      ) : (
        <EmptyState>
          No URLs added yet. Enter a URL above to start scraping.
        </EmptyState>
      )}
    </DownloadListContainer>
  );
};

export default DownloadList;
