import React, { useState } from 'react';
import { DownloadListProps, Resource } from '../types';
import Button from './Button';
import LoadingSpinner from './LoadingSpinner';

const DownloadList: React.FC<DownloadListProps> = ({
  resources,
  onDownload,
  isDownloading,
  downloadProgress
}) => {
  const [showDetails, setShowDetails] = useState(false);
  const [filterType, setFilterType] = useState<string>('all');

  const getFileIcon = (type: string) => {
    if (type.includes('image')) return 'ðŸ–¼ï¸';
    if (type.includes('css')) return 'ðŸŽ¨';
    if (type.includes('javascript') || type.includes('js')) return 'âš¡';
    if (type.includes('html')) return 'ðŸ“„';
    if (type.includes('font')) return 'ðŸ”¤';
    if (type.includes('json')) return 'ðŸ“‹';
    return 'ðŸ“';
  };

  const getFileSize = (size: number) => {
    if (size < 1024) return `${size} B`;
    if (size < 1024 * 1024) return `${(size / 1024).toFixed(1)} KB`;
    return `${(size / (1024 * 1024)).toFixed(1)} MB`;
  };

  const filteredResources = filterType === 'all' 
    ? resources 
    : resources.filter(resource => resource.type.includes(filterType));

  const resourceTypes = [
    { key: 'all', label: 'All', count: resources.length },
    { key: 'image', label: 'Images', count: resources.filter(r => r.type.includes('image')).length },
    { key: 'css', label: 'CSS', count: resources.filter(r => r.type.includes('css')).length },
    { key: 'javascript', label: 'JavaScript', count: resources.filter(r => r.type.includes('javascript') || r.type.includes('js')).length },
    { key: 'html', label: 'HTML', count: resources.filter(r => r.type.includes('html')).length },
    { key: 'font', label: 'Fonts', count: resources.filter(r => r.type.includes('font')).length },
  ];

  return (
    <div className="card max-w-6xl mx-auto">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-xl font-bold text-gray-900 dark:text-white">
            Extracted Resources
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400">
            {resources.length} resources found
          </p>
        </div>
        
        <div className="flex items-center space-x-3">
          <Button
            onClick={() => setShowDetails(!showDetails)}
            variant="outline"
            size="sm"
          >
            {showDetails ? 'Hide Details' : 'Show Details'}
          </Button>
          
          <Button
            onClick={onDownload}
            disabled={isDownloading}
            variant="secondary"
            size="lg"
          >
            {isDownloading ? (
              <div className="flex items-center space-x-2">
                <LoadingSpinner size="sm" color="white" />
                <span>Creating ZIP...</span>
              </div>
            ) : (
              <div className="flex items-center space-x-2">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
                <span>Download ZIP</span>
              </div>
            )}
          </Button>
        </div>
      </div>

      {/* Filter Tabs */}
      <div className="flex flex-wrap gap-2 mb-6">
        {resourceTypes.map(({ key, label, count }) => (
          <button
            key={key}
            onClick={() => setFilterType(key)}
            className={`
              px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200
              ${filterType === key
                ? 'bg-primary-500 text-white shadow-lg'
                : 'bg-gray-100 dark:bg-dark-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-dark-600'
              }
            `}
          >
            {label} ({count})
          </button>
        ))}
      </div>

      {/* Resources List */}
      {showDetails && (
        <div className="space-y-3 max-h-96 overflow-y-auto">
          {filteredResources.map((resource, index) => (
            <div
              key={index}
              className="flex items-center space-x-3 p-3 bg-gray-50 dark:bg-dark-700 rounded-lg hover:bg-gray-100 dark:hover:bg-dark-600 transition-colors"
            >
              <span className="text-lg">{getFileIcon(resource.type)}</span>
              
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium text-gray-900 dark:text-white truncate">
                  {resource.saveAs}
                </p>
                <p className="text-xs text-gray-500 dark:text-gray-400 truncate">
                  {resource.url}
                </p>
              </div>
              
              <div className="flex items-center space-x-4 text-xs text-gray-500 dark:text-gray-400">
                <span className="bg-gray-200 dark:bg-dark-600 px-2 py-1 rounded">
                  {resource.type}
                </span>
                <span>{getFileSize(resource.size)}</span>
                <span className="bg-blue-100 dark:bg-blue-900/30 text-blue-800 dark:text-blue-200 px-2 py-1 rounded">
                  {resource.source}
                </span>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Summary */}
      <div className="mt-6 pt-6 border-t border-gray-200 dark:border-dark-600">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
          <div>
            <p className="text-2xl font-bold text-primary-600">{resources.length}</p>
            <p className="text-sm text-gray-600 dark:text-gray-400">Total Files</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-secondary-600">
              {getFileSize(resources.reduce((sum, r) => sum + r.size, 0))}
            </p>
            <p className="text-sm text-gray-600 dark:text-gray-400">Total Size</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-green-600">
              {resources.filter(r => r.source === 'NETWORK').length}
            </p>
            <p className="text-sm text-gray-600 dark:text-gray-400">Network</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-purple-600">
              {resources.filter(r => r.source === 'STATIC').length}
            </p>
            <p className="text-sm text-gray-600 dark:text-gray-400">Static</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DownloadList;
