import { useState, useEffect } from 'react';

const lightTheme = {
  primaryColor: '#007bff',
  secondaryColor: '#6c757d',
  backgroundColor: '#f8f9fa',
  textColor: '#212529',
  borderColor: '#dee2e6',
  borderRadius: '8px',
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  successColor: '#28a745',
  dangerColor: '#dc3545',
  warningColor: '#ffc107',
  infoColor: '#17a2b8',
  lightColor: '#f8f9fa',
  darkColor: '#343a40',
  transition: 'all 0.3s ease-in-out',
};

const darkTheme = {
  ...lightTheme,
  primaryColor: '#00ff8a', // Electric Green
  secondaryColor: '#a0a0a0',
  backgroundColor: '#1a1a1a',
  textColor: '#f0f0f0',
  borderColor: '#333333',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
  lightColor: '#2c2c2c',
  darkColor: '#1a1a1a',
};

export const useAppTheme = () => {
  const [themeName, setThemeName] = useState('dark'); // Default to dark mode

  const toggleTheme = () => {
    setThemeName(themeName === 'light' ? 'dark' : 'light');
  };

  useEffect(() => {
    // This could be used to save the theme to localStorage
  }, [themeName]);

  const theme = themeName === 'light' ? lightTheme : darkTheme;

  return { theme, toggleTheme, themeName };
};
