import React, { useState, useReducer, useCallback } from 'react';
import styled from 'styled-components';
import Button from '../Button';
import { FaTrash, FaDownload, FaPlus, FaCog, FaInfoCircle, FaExclamationTriangle } from 'react-icons/fa';
import axios from 'axios';

const DownloadListContainer = styled.div`
  background: white;
  border-radius: ${props => props.theme.borderRadius};
  box-shadow: ${props => props.theme.boxShadow};
  overflow: hidden;
`;

const DownloadListHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid ${props => props.theme.borderColor};
  font-size: 18px;
  font-weight: 600;
  color: ${props => props.theme.textColor};
`;

const UrlInputSection = styled.div`
  padding: 20px;
  border-bottom: 1px solid ${props => props.theme.borderColor};
`;

const UrlInput = styled.input`
  width: 100%;
  padding: 12px 16px;
  border: 2px solid ${props => props.theme.borderColor};
  border-radius: ${props => props.theme.borderRadius};
  font-size: 14px;
  transition: ${props => props.theme.transition};
  
  &:focus {
    outline: none;
    border-color: ${props => props.theme.primaryColor};
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 15px;
  flex-wrap: wrap;
  align-items: center;
`;

const DownloadListItems = styled.div`
  max-height: 400px;
  overflow-y: auto;
`;

const DownloadListItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 15px 20px;
  border-bottom: 1px solid ${props => props.theme.borderColor};
  transition: ${props => props.theme.transition};
  
  &:hover {
    background-color: ${props => props.theme.lightColor};
  }
  
  ${props => props.highlighted && `
    background-color: ${props.theme.primaryColor}10;
    border-left: 4px solid ${props.theme.primaryColor};
  `}
`;

const ItemUrl = styled.div`
  flex: 1;
  font-size: 14px;
  color: ${props => props.theme.textColor};
  word-break: break-all;
`;

const ItemInfo = styled.div`
  font-size: 12px;
  color: ${props => props.theme.secondaryColor};
  margin-top: 5px;
`;

const ItemActions = styled.div`
  display: flex;
  gap: 8px;
  align-items: center;
`;

const Spinner = styled.div`
  width: 16px;
  height: 16px;
  border: 2px solid ${props => props.theme.borderColor};
  border-top: 2px solid ${props => props.theme.primaryColor};
  border-radius: 50%;
  animation: spin 1s linear infinite;
  
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const OptionsSection = styled.div`
  padding: 20px;
  border-bottom: 1px solid ${props => props.theme.borderColor};
`;

const OptionItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 10px;
`;

const OptionLabel = styled.label`
  font-size: 14px;
  color: ${props => props.theme.textColor};
  cursor: pointer;
`;

const Toggle = styled.input`
  width: 40px;
  height: 20px;
  appearance: none;
  background-color: ${props => props.theme.borderColor};
  border-radius: 10px;
  position: relative;
  cursor: pointer;
  transition: ${props => props.theme.transition};
  
  &:checked {
    background-color: ${props => props.theme.primaryColor};
  }
  
  &:before {
    content: '';
    position: absolute;
    width: 16px;
    height: 16px;
    border-radius: 50%;
    background-color: white;
    top: 2px;
    left: 2px;
    transition: ${props => props.theme.transition};
  }
  
  &:checked:before {
    transform: translateX(20px);
  }
`;

const ErrorMessage = styled.div`
  background-color: ${props => props.theme.dangerColor}10;
  border: 1px solid ${props => props.theme.dangerColor};
  color: ${props => props.theme.dangerColor};
  padding: 10px;
  border-radius: ${props => props.theme.borderRadius};
  margin-top: 10px;
  font-size: 14px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const SuccessMessage = styled.div`
  background-color: ${props => props.theme.successColor}10;
  border: 1px solid ${props => props.theme.successColor};
  color: ${props => props.theme.successColor};
  padding: 10px;
  border-radius: ${props => props.theme.borderRadius};
  margin-top: 10px;
  font-size: 14px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const DebugInfo = styled.div`
  background-color: ${props => props.theme.infoColor}10;
  border: 1px solid ${props => props.theme.infoColor};
  color: ${props => props.theme.infoColor};
  padding: 10px;
  border-radius: ${props => props.theme.borderRadius};
  margin-top: 10px;
  font-size: 12px;
`;

const DownloadList = () => {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [downloadList, setDownloadList] = useState([]);
  const [resources, setResources] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [debugInfo, setDebugInfo] = useState(null);
  const [options, setOptions] = useState({
    ignoreNoContentFile: true,
    beautifyFile: true,
  });

  const handleAddUrl = useCallback(async () => {
    if (!url.trim()) return;
    
    setIsScraping(true);
    setError('');
    setSuccess('');
    setDebugInfo(null);
    
    try {
      const response = await axios.post('/api/scrape-debug', {
        url: url.trim(),
        options
      });
      
      if (response.data.success) {
        setResources(response.data.resources);
        setDownloadList(prev => [...prev, { 
          url: url.trim(), 
          resources: response.data.resources,
          debug: response.data.debug
        }]);
        setUrl('');
        setSuccess(`Successfully scraped ${response.data.count} resources from ${url.trim()}`);
        setDebugInfo(response.data.debug);
      }
    } catch (error) {
      console.error('Scraping error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to scrape website. Please check the URL and try again.';
      setError(errorMessage);
    } finally {
      setIsScraping(false);
    }
  }, [url, options]);

  const handleDownload = useCallback(async (item) => {
    try {
      const response = await axios.post('/api/download', {
        resources: item.resources,
        options
      }, {
        responseType: 'blob'
      });
      
      // Create download link
      const blob = new Blob([response.data]);
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = `${new URL(item.url).hostname}.zip`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(downloadUrl);
      
      setSuccess(`Downloaded ${item.resources.length} resources as ZIP file`);
    } catch (error) {
      console.error('Download error:', error);
      setError('Failed to download file. Please try again.');
    }
  }, [options]);

  const handleRemoveItem = useCallback((index) => {
    setDownloadList(prev => prev.filter((_, i) => i !== index));
  }, []);

  const handleReset = useCallback(() => {
    setDownloadList([]);
    setResources([]);
    setError('');
    setSuccess('');
    setDebugInfo(null);
  }, []);

  const handleOptionChange = useCallback((key, value) => {
    setOptions(prev => ({ ...prev, [key]: value }));
  }, []);

  const estimateDownloadTime = (bytes) => {
    if (!bytes) return '';
    // Assume average 5 Mbps connection
    const bits = bytes * 8;
    const speed = 5 * 1000 * 1000; // 5 Mbps in bits
    const seconds = Math.ceil(bits / speed);
    
    if (seconds < 2) return '~1s';
    if (seconds < 60) return `~${seconds}s`;
    
    const minutes = Math.ceil(seconds / 60);
    return `~${minutes}min`;
  };

  const formatFileSize = (bytes) => {
    if (!bytes || bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <DownloadListContainer>
      <DownloadListHeader>Download List</DownloadListHeader>
      
      <OptionsSection>
        <OptionItem>
          <OptionLabel>Ignore files with no content</OptionLabel>
          <Toggle
            type="checkbox"
            checked={options.ignoreNoContentFile}
            onChange={(e) => handleOptionChange('ignoreNoContentFile', e.target.checked)}
          />
        </OptionItem>
        <OptionItem>
          <OptionLabel>Beautify files (HTML, CSS, JS, JSON)</OptionLabel>
          <Toggle
            type="checkbox"
            checked={options.beautifyFile}
            onChange={(e) => handleOptionChange('beautifyFile', e.target.checked)}
          />
        </OptionItem>
      </OptionsSection>
      
      <UrlInputSection>
        <UrlInput
          type="url"
          placeholder="Enter website URL (e.g., https://example.com)"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleAddUrl()}
          disabled={isScraping}
        />
        <ButtonGroup>
          <Button
            color="primary"
            onClick={handleAddUrl}
            disabled={isScraping || !url.trim()}
          >
            {isScraping ? <Spinner /> : <FaPlus />}
            {isScraping ? 'Scraping...' : 'Add URL'}
          </Button>
          <Button
            color="danger"
            onClick={handleReset}
            disabled={isScraping}
          >
            Reset All
          </Button>
          {isScraping && <Spinner />}
        </ButtonGroup>
        
        {error && (
          <ErrorMessage>
            <FaExclamationTriangle />
            {error}
          </ErrorMessage>
        )}
        
        {success && (
          <SuccessMessage>
            <FaInfoCircle />
            {success}
          </SuccessMessage>
        )}
        
        {debugInfo && (
          <DebugInfo>
            <strong>Debug Info:</strong><br />
            • Total Resources: {debugInfo.totalResources}<br />
            • Unique Domains: {debugInfo.uniqueDomains}<br />
            • Scrape Time: {debugInfo.scrapeTime}ms<br />
            • Total Size: {formatFileSize(debugInfo.sizeStats.total)}<br />
            • Resource Types: {Object.keys(debugInfo.resourceTypes).join(', ')}
          </DebugInfo>
        )}
      </UrlInputSection>
      
      <DownloadListItems>
        {downloadList.map((item, index) => (
          <DownloadListItem key={index} highlighted={index === 0}>
            <div>
              <ItemUrl>{item.url}</ItemUrl>
              {item.debug && (
                <ItemInfo>
                  {item.debug.totalResources} resources • {formatFileSize(item.debug.totalSize)} • {item.debug.scrapeTime}ms
                </ItemInfo>
              )}
            </div>
            <ItemActions>
              <Button
                color="success"
                onClick={() => handleDownload(item)}
                disabled={!item.resources || item.resources.length === 0}
              >
                <FaDownload />
                Download ({item.debug.totalResources})
              </Button>
              <small>{estimateDownloadTime(item.debug.totalSize)}</small>
              <Button
                color="danger"
                onClick={() => handleRemoveItem(index)}
              >
                <FaTrash />
              </Button>
            </ItemActions>
          </DownloadListItem>
        ))}
        {downloadList.length === 0 && (
          <div style={{ padding: '40px 20px', textAlign: 'center', color: '#666' }}>
            No URLs added yet. Enter a URL above to start scraping.
          </div>
        )}
      </DownloadListItems>
    </DownloadListContainer>
  );
};

export default DownloadList;
