import { useMemo } from 'react';

const techNoirTheme = {
  primaryColor: '#e027ac',      // Vibrant Magenta
  secondaryColor: '#00f5d4',    // Bright Cyan
  backgroundColor: '#0a0f1d',  // Deep Blue/Black
  textColor: '#e6e6e6',         // Off-White
  borderColor: '#2a314b',       // Muted Blue
  borderRadius: '8px',
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.4)',
  successColor: '#00f5d4',      // Bright Cyan
  dangerColor: '#ff3d71',       // Hot Pink/Red
  warningColor: '#ffc107',
  infoColor: '#17a2b8',
  lightColor: '#161b29',       // Darker Blue for hover
  darkColor: '#0a0f1d',
  transition: 'all 0.2s ease-in-out',
};

export const useAppTheme = () => {
  return useMemo(() => techNoirTheme, []);
};
