const DownloadList = () => {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [scrapeResult, setScrapeResult] = useState(null);
  const [error, setError] = useState('');
  const [isDownloading, setIsDownloading] = useState(false);
  const [isPolling, setIsPolling] = useState(false);
  const [downloadProgress, setDownloadProgress] = useSimulatedProgress(isDownloading || isPolling);
  const [options, setOptions] = useState({
    ignoreNoContentFile: true,
    beautifyFile: true,
  });

  const handleAddUrl = useCallback(async () => {
    if (!url.trim()) return;

    setIsScraping(true);
    setError('');
    setScrapeResult(null);

    try {
      const response = await axios.post('/api/scrape-debug', {
        url: url.trim(),
        options
      });

      if (response.data.success) {
        setScrapeResult({
          url: response.data.url,
          resources: response.data.resources,
          debug: response.data.debug
        });
        setUrl('');
      }
    } catch (error) {
      console.error('Scraping error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to scrape website. Please check the URL and try again.';
      setError(errorMessage);
    } finally {
      setIsScraping(false);
    }
  }, [url, options]);

  useInterval(async () => {
    if (!isPolling || !scrapeResult?.jobId) return;

    try {
      const { data } = await axios.get(`/api/zip-status/${scrapeResult.jobId}`);
      if (data.success && data.status === 'COMPLETE') {
        setIsPolling(false);
        setIsDownloading(true); // Keep showing progress
        setDownloadProgress(100); // Jump to 100

        // Trigger download
        window.location.href = `/api/download-zip/${scrapeResult.jobId}`;

        setTimeout(() => {
          setIsDownloading(false);
          setScrapeResult(null); // Clear the report after download
        }, 1000); // Keep report visible for a bit
      } else if (data.success && data.status === 'ERROR') {
        setIsPolling(false);
        setError('Server failed to create ZIP file.');
      }
    } catch (err) {
      setIsPolling(false);
      setError('Could not get download status.');
    }
  }, isPolling ? 2000 : null); // Poll every 2 seconds

  const handleDownload = useCallback(() => {
    if (scrapeResult?.jobId) {
      setIsPolling(true);
    }
  }, [scrapeResult]);

  const handleOptionChange = useCallback((key, value) => {
    setOptions(prev => ({ ...prev, [key]: value }));
  }, []);

  const formatFileSize = (bytes) => {
    if (!bytes || bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <DownloadListContainer>
      <DownloadListHeader>Download List</DownloadListHeader>

      <OptionsSection>
        <OptionItem>
          <OptionLabel>Ignore files with no content</OptionLabel>
          <Toggle
            type="checkbox"
            checked={options.ignoreNoContentFile}
            onChange={(e) => handleOptionChange('ignoreNoContentFile', e.target.checked)}
          />
        </OptionItem>
        <OptionItem>
          <OptionLabel htmlFor="beautifyFile">Beautify files (HTML, CSS, JS, JSON)</OptionLabel>
          <Toggle id="beautifyFile" type="checkbox" checked={options.beautifyFile} onChange={e => handleOptionChange('beautifyFile', e.target.checked)} />
        </OptionItem>
      </OptionsSection>

      <UrlInputSection>
        <UrlInput
          type="url"
          placeholder="Enter website URL (e.g., https://example.com)"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleAddUrl()}
          disabled={isScraping}
        />
        <ButtonGroup>
          <Button 
            onClick={handleAddUrl} 
            disabled={isScraping || !url.trim()}
            color="primary"
          >
            {isScraping ? (
              <><Spinner /> Scraping...</>
            ) : (
              <><FaPlus /> Add URL</>
            )}
          </Button>
        </ButtonGroup>

        {error && (
          <ErrorMessage>
            <FaExclamationTriangle /> {error}
          </ErrorMessage>
        )}

        {scrapeResult && (
          <ScrapeReport>
            <ReportDetails>
              <DebugTitle>
                <FaCheckCircle /> Scrape Successful
              </DebugTitle>

              <DebugItem style={{ marginBottom: '20px' }}>
                <DebugKey>URL</DebugKey>
                <DebugValue>{scrapeResult.url}</DebugValue>
              </DebugItem>

              <DebugGrid>
                <DebugItem>
                  <DebugKey>Total Resources</DebugKey>
                  <DebugValue>{scrapeResult.debug.totalResources}</DebugValue>
                </DebugItem>
                <DebugItem>
                  <DebugKey>Total Size</DebugKey>
                  <DebugValue>{formatFileSize(scrapeResult.debug.totalSize)}</DebugValue>
                </DebugItem>
                <DebugItem>
                  <DebugKey>Scrape Time</DebugKey>
                  <DebugValue>{scrapeResult.debug.scrapeTime}ms</DebugValue>
                </DebugItem>
                <DebugItem>
                  <DebugKey>Unique Domains</DebugKey>
                  <DebugValue>{scrapeResult.debug.uniqueDomains}</DebugValue>
                </DebugItem>
              </DebugGrid>
            </ReportDetails>
            <ReportActions>
              <Button 
                onClick={handleDownload}
                disabled={isDownloading || isPolling}
                color="primary"
                progress={(isDownloading || isPolling) ? downloadProgress : 0}
              >
                {(isDownloading || isPolling) ? (
                  `Processing... ${downloadProgress}%`
                ) : (
                  <><FaDownload /> Download</>
                )}
              </Button>
              <Button 
                onClick={() => setScrapeResult(null)} 
                color="danger"
              >
                <FaTrash /> Delete
              </Button>
            </ReportActions>
          </ScrapeReport>
        )}
      </UrlInputSection>

      {!scrapeResult && (
        <EmptyState>
          No results yet. Enter a URL above to start scraping.
        </EmptyState>
      )}
    </DownloadListContainer>
  );
};

export default DownloadList;
