import React from 'react';
import styled from 'styled-components';
import { FaBolt } from 'react-icons/fa';

const FooterBar = styled.footer`
  width: 100%;
  background: rgba(10, 20, 40, 0.98);
  border-top: 1px solid #223;
  padding: 0;
  margin-top: 40px;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 0 -2px 16px rgba(0,0,0,0.08);
`;

const FooterMain = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-start;
  padding: 32px 24px 16px 24px;
  flex-wrap: wrap;
  gap: 32px;

  @media (max-width: 700px) {
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 16px;
  }
`;

const Brand = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

const BrandIcon = styled.div`
  color: #4CAF50;
  font-size: 2rem;
`;

const BrandName = styled.div`
  font-size: 1.5rem;
  font-weight: 700;
  color: #fff;
  letter-spacing: 1px;
`;

const Tagline = styled.div`
  color: #b0eaff;
  font-size: 1rem;
  margin-top: 4px;
`;

const Contact = styled.div`
  color: #ccc;
  font-size: 1rem;
  line-height: 1.7;
`;

const Copyright = styled.div`
  color: #888;
  font-size: 13px;
  text-align: center;
  width: 100%;
  padding-bottom: 12px;
`;

const Footer = () => {
  return (
    <FooterBar>
      <FooterMain>
        <div>
          <Brand>
            <BrandIcon><FaBolt /></BrandIcon>
            <BrandName>CloneCraft</BrandName>
          </Brand>
          <Tagline>Lightning-fast website resource scraper</Tagline>
        </div>
        <Contact>
          <div><b>Contact:</b></div>
          <div>Email: <a href="mailto:support@clonecraft.com" style={{color:'#b0eaff'}}>support@clonecraft.com</a></div>
          <div>Phone: <a href="tel:+1234567890" style={{color:'#b0eaff'}}>+1 (234) 567-890</a></div>
        </Contact>
      </FooterMain>
      <Copyright>
        &copy; {new Date().getFullYear()} CloneCraft. All rights reserved.
      </Copyright>
    </FooterBar>
  );
};

export default Footer;
